mapper.select:
SELECT * FROM test_issues
WHERE id IN

    #{id}

SELECT *
FROM test_issues
WHERE sample_id = #{sampleId}
  AND (
        (created_by = 'tester' AND history_id = (
            SELECT MAX(history_id)
            FROM test_issues
            WHERE sample_id = #{sampleId}
              AND created_by = 'tester'
        ))
        OR created_by != 'tester'
    )
