mapper.update:
UPDATE samples
SET
    sample_schedule =#{sample_schedule}
WHERE
    sample_id =#{sample_id}

UPDATE samples
SET
    sample_schedule =#{sample_schedule},
    finish_time =#{finish_time}
WHERE
    sample_id =#{sample_id}

mapper.select:
SELECT create_time FROM samples
WHERE
        sample_id =#{sample_id}

SELECT planfinish_time FROM samples
WHERE
    sample_id =#{sample_id}

SELECT sample_name FROM samples
WHERE
    sample_id =#{sample_id}

SELECT tester_teamwork FROM samples
WHERE
        sample_id =#{sample_id}

SELECT tester FROM samples
WHERE
    sample_id =#{sample_id}

SELECT sample_id FROM samples
WHERE
    filepath = #{filepath}

SELECT COALESCE(MAX(history_id), 0) + 1
FROM test_issues
WHERE sample_id = #{sample_id}

mapper.update:
UPDATE samples
SET
    planTestDuration =#{planTestDuration},
    testDuration =#{testDuration}
WHERE
    sample_id =#{sample_id}
